mapper.update:
UPDATE reservation
SET booker_name = #{bookerName},
    booker_email = #{bookerEmail},
    booker_phone = #{bookerPhone}
WHERE reservation_id = #{reservationId}

UPDATE reservation
SET status = 'PAID'
WHERE reservation_id = #{reservationId}

UPDATE reservation
SET status = 'USED'
WHERE reservation_id = #{reservationId}

UPDATE reservation
SET status = 'CANCELED'
WHERE reservation_id = #{reservationId}
